import React, { useEffect, useState } from 'react';
import axios from 'axios';

const EmployeeList = () => {
  const [employees, setEmployees] = useState([]);
  const [query, setQuery] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editEmployee, setEditEmployee] = useState(null);
  const [loading, setLoading] = useState(true);

  // Fetch employees
  const fetchEmployees = async () => {
    setLoading(true);
    try {
      console.log('Fetching employees...'); // Debug
      const response = await axios.get('/api/employees');
      console.log('Employees fetched:', response.data); // Debug
      setEmployees(response.data);
    } catch (error) {
      console.error('Error fetching employees:', error);
      alert('Error loading employees: ' + (error.response?.data?.message || error.message));
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  // Filter employees
  const filteredEmployees = employees.filter(emp =>
    emp.name?.toLowerCase().includes(query.toLowerCase()) ||
    emp.email?.toLowerCase().includes(query.toLowerCase()) ||
    emp.department?.toLowerCase().includes(query.toLowerCase())
  );

  // Add Employee
  const handleAdd = () => {
    console.log('Add employee clicked'); // Debug
    setEditEmployee(null);
    setShowModal(true);
  };

  // Edit Employee  
  const handleEdit = (employee) => {
    console.log('Edit employee clicked:', employee); // Debug
    setEditEmployee(employee);
    setShowModal(true);
  };

  // Delete Employee
  const handleDelete = async (id, name) => {
    if (window.confirm(`Are you sure you want to delete "${name}"?`)) {
      try {
        console.log('Deleting employee:', id); // Debug
        await axios.delete(`/api/employees/${id}`);
        await fetchEmployees(); // Refresh
        alert('Employee deleted successfully!');
      } catch (error) {
        console.error('Delete error:', error);
        alert('Error deleting employee: ' + (error.response?.data?.message || error.message));
      }
    }
  };

  if (loading) {
    return (
      <div id="employeesSection" className="main-content">
        <div className="section-header">
          <h1>Employee Management</h1>
        </div>
        <div className="text-center">Loading employees...</div>
      </div>
    );
  }

  return (
    <>
      <div id="employeesSection" className="main-content">
        <div className="section-header">
          <h1>Employee Management</h1>
          <button className="btn btn--primary" onClick={handleAdd}>
            Add Employee
          </button>
        </div>

        <div className="search-container">
          <input
            className="form-control"
            placeholder="Search employees by name, email, or department..."
            value={query}
            onChange={(e) => setQuery(e.target.value)}
          />
        </div>

        <div className="table-container">
          <table className="data-table">
            <thead>
              <tr>
                <th>Name</th>
                <th>Email</th>
                <th>Phone</th>
                <th>Department</th>
                <th>Status</th>
                <th>Actions</th>
              </tr>
            </thead>
            <tbody>
              {filteredEmployees.length === 0 ? (
                <tr>
                  <td colSpan="6" className="text-center">
                    <div className="empty-state">
                      <div className="empty-icon">ðŸ‘¥</div>
                      <p>No employees found</p>
                      <small>Total employees: {employees.length}</small>
                    </div>
                  </td>
                </tr>
              ) : (
                filteredEmployees.map(emp => (
                  <tr key={emp._id}>
                    <td>{emp.name}</td>
                    <td>{emp.email}</td>
                    <td>{emp.phone}</td>
                    <td>{emp.department}</td>
                    <td>
                      <span className={`status-badge ${emp.status === 'Active' ? 'status-active' : 'status-inactive'}`}>
                        {emp.status?.toUpperCase()}
                      </span>
                    </td>
                    <td>
                      <div className="action-buttons">
                        <button 
                          className="btn btn--sm btn--secondary" 
                          onClick={() => handleEdit(emp)}
                        >
                          Edit
                        </button>
                        <button 
                          className="btn btn--sm btn--outline" 
                          onClick={() => handleDelete(emp._id, emp.name)}
                        >
                          Delete
                        </button>
                      </div>
                    </td>
                  </tr>
                ))
              )}
            </tbody>
          </table>
        </div>
      </div>

      {showModal && (
        <EmployeeModal
          employee={editEmployee}
          onClose={() => setShowModal(false)}
          onRefresh={fetchEmployees}
        />
      )}
    </>
  );
};

// Employee Modal Component
const EmployeeModal = ({ employee, onClose, onRefresh }) => {
  const [form, setForm] = useState({
    name: '',
    email: '',
    phone: '',
    department: '',
    status: 'Active'
  });
  const [saving, setSaving] = useState(false);

  useEffect(() => {
    if (employee) {
      setForm({
        name: employee.name || '',
        email: employee.email || '',
        phone: employee.phone || '',
        department: employee.department || '',
        status: employee.status || 'Active'
      });
    }
  }, [employee]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Submitting form:', form); // Debug

    // Validation
    if (!form.name?.trim() || !form.email?.trim() || !form.phone?.trim() || !form.department) {
      alert('Please fill all required fields');
      return;
    }

    setSaving(true);
    try {
      if (employee) {
        // Update
        console.log('Updating employee:', employee._id); // Debug
        await axios.put(`/api/employees/${employee._id}`, form);
        alert('Employee updated successfully!');
      } else {
        // Create
        console.log('Creating new employee'); // Debug
        await axios.post('/api/employees', form);
        alert('Employee added successfully!');
      }

      await onRefresh(); // Refresh list
      onClose(); // Close modal
    } catch (error) {
      console.error('Save error:', error); // Debug
      const errorMsg = error.response?.data?.message || error.response?.data?.errors?.[0]?.msg || error.message;
      alert('Error saving employee: ' + errorMsg);
    } finally {
      setSaving(false);
    }
  };

  return (
    <div className="modal" onClick={(e) => e.target.classList.contains('modal') && onClose()}>
      <div className="modal-content">
        <div className="modal-header">
          <h2>{employee ? 'Edit Employee' : 'Add Employee'}</h2>
          <button className="modal-close" onClick={onClose}>Ã—</button>
        </div>
        <div className="modal-body">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Name *</label>
              <input
                type="text"
                className="form-control"
                value={form.name}
                onChange={(e) => setForm({ ...form, name: e.target.value })}
                placeholder="Enter employee name"
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Email *</label>
              <input
                type="email"
                className="form-control"
                value={form.email}
                onChange={(e) => setForm({ ...form, email: e.target.value })}
                placeholder="Enter email address"
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Phone *</label>
              <input
                type="tel"
                className="form-control"
                value={form.phone}
                onChange={(e) => setForm({ ...form, phone: e.target.value })}
                placeholder="Enter phone number"
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Department *</label>
              <select
                className="form-control"
                value={form.department}
                onChange={(e) => setForm({ ...form, department: e.target.value })}
                required
              >
                <option value="">Select Department</option>
                <option value="Engineering">Engineering</option>
                <option value="Marketing">Marketing</option>
                <option value="HR">HR</option>
                <option value="Sales">Sales</option>
                <option value="Finance">Finance</option>
                <option value="Operations">Operations</option>
              </select>
            </div>

            <div className="form-group">
              <label className="form-label">Status</label>
              <select
                className="form-control"
                value={form.status}
                onChange={(e) => setForm({ ...form, status: e.target.value })}
              >
                <option value="Active">Active</option>
                <option value="Inactive">Inactive</option>
              </select>
            </div>

            <div className="modal-actions">
              <button type="button" className="btn btn--outline" onClick={onClose}>
                Cancel
              </button>
              <button type="submit" className="btn btn--primary" disabled={saving}>
                {saving ? 'Saving...' : (employee ? 'Update Employee' : 'Add Employee')}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
};

export default EmployeeList;
