import React, {useState} from 'react';

const LoginForm = ({onLogin, error, loading}) => {
  const [email,setEmail] = useState('');
  const [password,setPassword] = useState('');

  const submit = (e)=>{ e.preventDefault(); onLogin && onLogin(email,password); };

  return (
    <div id="loginPage" className="login-container">
      <div className="login-form">
        <h1>Admin Login</h1>
        <p className="login-subtitle">Employee Management System</p>

        <form onSubmit={submit}>
          <div className="form-group">
            <label className="form-label" htmlFor="email">Email</label>
            <input id="email" type="email" className="form-control" value={email} onChange={e=>setEmail(e.target.value)} placeholder="Enter your email" required />
          </div>

          <div className="form-group">
            <label className="form-label" htmlFor="password">Password</label>
            <input id="password" type="password" className="form-control" value={password} onChange={e=>setPassword(e.target.value)} placeholder="Enter your password" required />
          </div>

          <button type="submit" className="btn btn--primary btn--full-width" disabled={loading}>
            {loading?'Logging in...':'Login'}
          </button>

          {error && <div className="form-error" style={{marginTop:8}}>{error}</div>}
        </form>

        <div className="login-info">
          <p><strong>Demo Credentials:</strong></p>
          <p>Email: admin@company.com</p>
          <p>Password: admin123</p>
        </div>
      </div>
    </div>
  );
};

export default LoginForm;
