import React, {useEffect, useState} from 'react';
import Navbar from './components/common/Navbar';
import LoginForm from './components/auth/LoginForm';
import DashboardSection from './components/dashboard/DashboardSection';
import EmployeeList from './components/employees/EmployeeList';
import TaskList from './components/tasks/TaskList';

const sampleEmployees = [
  {id:1,name:'John Doe',email:'john.doe@company.com',phone:'+1-234-567-8901',department:'Engineering',status:'Active'},
  {id:2,name:'Jane Smith',email:'jane.smith@company.com',phone:'+1-234-567-8902',department:'Marketing',status:'Active'},
  {id:3,name:'Mike Johnson',email:'mike.johnson@company.com',phone:'+1-234-567-8903',department:'HR',status:'Inactive'}
];

const sampleTasks = [
  {id:1,title:'Develop User Authentication',description:'Implement JWT-based authentication system for the application',assignedEmployee:1,assignedEmployeeName:'John Doe',priority:'High',deadline:'2025-09-15',status:'In Progress'},
  {id:2,title:'Marketing Campaign Analysis',description:'Analyze the effectiveness of the Q1 marketing campaigns',assignedEmployee:2,assignedEmployeeName:'Jane Smith',priority:'Medium',deadline:'2025-09-28',status:'Pending'},
  {id:3,title:'Employee Onboarding Process',description:'Update and streamline the new employee onboarding process',assignedEmployee:3,assignedEmployeeName:'Mike Johnson',priority:'Low',deadline:'2025-10-10',status:'Completed'}
];

const App = () => {
  const [auth,setAuth] = useState(false);
  const [active,setActive] = useState('dashboard');
  const [employees,setEmployees] = useState(sampleEmployees);
  const [tasks,setTasks] = useState(sampleTasks);
  const [error,setError] = useState('');
  const [loading,setLoading] = useState(false);

  const login = async (email,pass)=>{
    setLoading(true);
    try{
      if(email==='admin@company.com' && pass==='admin123'){
        setAuth(true);
        setError('');
      } else {
        setError('Invalid credentials');
      }
    } finally { setLoading(false); }
  };

  const logout = ()=> setAuth(false);

  const addEmployee = ()=>{/* open modal - implement as needed */};
  const editEmployee = (e)=>{/* open modal */};
  const deleteEmployee = (e)=> setEmployees(prev=>prev.filter(x=>(x.id||x._id)!==(e.id||e._id)));

  const addTask = ()=>{/* open modal */};
  const editTask = (t)=>{/* open modal */};
  const deleteTask = (t)=> setTasks(prev=>prev.filter(x=>(x.id||x._id)!==(t.id||t._id)));

  useEffect(()=>{},[]);

  if(!auth) return <LoginForm onLogin={login} error={error} loading={loading} />;

  return (
    <>
      <Navbar active={active} onChange={setActive} onLogout={logout} />
      {active==='dashboard' && <DashboardSection employees={employees} tasks={tasks} onInit={()=>{}} />}
      {active==='employees' && (
        <EmployeeList
          employees={employees}
          onAdd={addEmployee}
          onEdit={editEmployee}
          onDelete={deleteEmployee}
          onSearch={()=>{}}
        />
      )}
      {active==='tasks' && (
        <TaskList
          tasks={tasks}
          onAdd={addTask}
          onEdit={editTask}
          onDelete={deleteTask}
        />
      )}
    </>
  );
};

export default App;
