import React, {useEffect, useMemo, useState} from 'react';

const EmployeeList = ({employees, onAdd, onEdit, onDelete, onSearch}) => {
  const [q,setQ] = useState('');
  const filtered = useMemo(()=>{
    const s=q.toLowerCase();
    return employees.filter(e =>
      e.name.toLowerCase().includes(s) ||
      e.email.toLowerCase().includes(s) ||
      e.department.toLowerCase().includes(s)
    );
  },[q,employees]);

  useEffect(()=>{ onSearch && onSearch(q); },[q,onSearch]);

  const badge = (status)=>
    <span className={`status-badge ${status==='Active'?'status-active':'status-inactive'}`}>{status.toUpperCase()}</span>;

  return (
    <section id="employeesSection" className="main-content">
      <div className="section-header">
        <h1>Employee Management</h1>
        <button className="btn btn--primary" onClick={onAdd}>Add Employee</button>
      </div>

      <div className="search-container">
        <input
          className="form-control"
          placeholder="Search employees by name, email, or department..."
          value={q}
          onChange={e=>setQ(e.target.value)}
        />
      </div>

      <div className="table-container">
        <table className="data-table">
          <thead>
            <tr>
              <th>Name</th><th>Email</th><th>Phone</th><th>Department</th><th>Status</th><th>Actions</th>
            </tr>
          </thead>
          <tbody id="employeeTableBody">
            {filtered.length===0 ? (
              <tr><td colSpan="6" className="text-center">
                <div className="empty-state"><div className="empty-icon">ðŸ‘¥</div><p>No employees found</p></div>
              </td></tr>
            ) : filtered.map(e=>(
              <tr key={e._id || e.id}>
                <td>{e.name}</td>
                <td>{e.email}</td>
                <td>{e.phone}</td>
                <td>{e.department}</td>
                <td>{badge(e.status)}</td>
                <td>
                  <div className="action-buttons">
                    <button className="btn btn--sm btn--secondary" onClick={()=>onEdit(e)}>Edit</button>
                    <button className="btn btn--sm btn--outline" onClick={()=>onDelete(e)}>Delete</button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </section>
  );
};

export default EmployeeList;
