import React, { useEffect, useState } from 'react';
import axios from 'axios';

const TaskList = () => {
  const [tasks, setTasks] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [showModal, setShowModal] = useState(false);
  const [editTask, setEditTask] = useState(null);

  const pri = (p) => p === 'High' ? 'priority-high' : p === 'Medium' ? 'priority-medium' : 'priority-low';
  const st = (s) => s === 'Pending' ? 'task-pending' : s === 'In Progress' ? 'task-progress' : 'task-completed';
  const fmt = (d) => new Date(d).toLocaleDateString();

  // Fetch tasks
  const fetchTasks = async () => {
    try {
      const response = await axios.get('/api/tasks');
      setTasks(response.data);
    } catch (error) {
      console.error('Error fetching tasks:', error);
    }
  };

  // Fetch employees for dropdown
  const fetchEmployees = async () => {
    try {
      const response = await axios.get('/api/employees');
      setEmployees(response.data.filter(emp => emp.status === 'Active'));
    } catch (error) {
      console.error('Error fetching employees:', error);
    }
  };

  useEffect(() => {
    fetchTasks();
    fetchEmployees();
  }, []);

  // Add Task
  const handleAdd = () => {
    setEditTask(null);
    setShowModal(true);
  };

  // Edit Task
  const handleEdit = (task) => {
    setEditTask(task);
    setShowModal(true);
  };

  // Delete Task
  const handleDelete = async (task) => {
    if (window.confirm(`Delete task "${task.title}"?`)) {
      try {
        await axios.delete(`/api/tasks/${task._id}`);
        fetchTasks(); // Refresh list
        alert('Task deleted successfully!');
      } catch (error) {
        alert('Error deleting task');
      }
    }
  };

  return (
    <>
      <section id="tasksSection" className="main-content">
        <div className="section-header">
          <h1>Task Management</h1>
          <button className="btn btn--primary" onClick={handleAdd}>Add Task</button>
        </div>

        <div className="table-container">
          <table className="data-table">
            <thead>
              <tr>
                <th>Title</th><th>Assigned To</th><th>Priority</th><th>Status</th><th>Deadline</th><th>Actions</th>
              </tr>
            </thead>
            <tbody id="taskTableBody">
              {tasks.length === 0 ? (
                <tr><td colSpan="6" className="text-center">
                  <div className="empty-state"><div className="empty-icon">ðŸ“‹</div><p>No tasks found</p></div>
                </td></tr>
              ) : tasks.map(t => (
                <tr key={t._id || t.id}>
                  <td>
                    <div>
                      <strong>{t.title}</strong>
                      <div className="text-sm opacity-50">
                        {(t.description || '').slice(0, 50)}{(t.description || '').length > 50 ? '...' : ''}
                      </div>
                    </div>
                  </td>
                  <td>{t.assignedEmployeeName}</td>
                  <td><span className={`status-badge ${pri(t.priority)}`}>{t.priority}</span></td>
                  <td><span className={`status-badge ${st(t.status)}`}>{t.status}</span></td>
                  <td>{fmt(t.deadline)}</td>
                  <td>
                    <div className="action-buttons">
                      <button className="btn btn--sm btn--secondary" onClick={() => handleEdit(t)}>Edit</button>
                      <button className="btn btn--sm btn--outline" onClick={() => handleDelete(t)}>Delete</button>
                    </div>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </section>

      {showModal && (
        <TaskModal
          task={editTask}
          employees={employees}
          onClose={() => setShowModal(false)}
          onSave={fetchTasks}
        />
      )}
    </>
  );
};

// Task Modal Component
function TaskModal({ task, employees, onClose, onSave }) {
  const [form, setForm] = useState({
    title: task?.title || '',
    description: task?.description || '',
    assignedEmployee: task?.assignedEmployee || '',
    priority: task?.priority || '',
    deadline: task?.deadline ? task.deadline.split('T')[0] : '',
    status: task?.status || 'Pending'
  });

  // Set default deadline to tomorrow for new tasks
  useEffect(() => {
    if (!task) {
      const tomorrow = new Date();
      tomorrow.setDate(tomorrow.getDate() + 1);
      setForm(prev => ({
        ...prev,
        deadline: tomorrow.toISOString().split('T')[0]
      }));
    }
  }, [task]);

  const handleSubmit = async (e) => {
    e.preventDefault();

    // Basic validation
    if (!form.title || !form.description || !form.assignedEmployee || !form.priority || !form.deadline) {
      alert('Please fill all fields');
      return;
    }

    try {
      if (task) {
        // Update existing task
        await axios.put(`/api/tasks/${task._id}`, form);
        alert('Task updated successfully!');
      } else {
        // Create new task
        await axios.post('/api/tasks', form);
        alert('Task added successfully!');
      }

      onSave(); // Refresh list
      onClose(); // Close modal
    } catch (error) {
      alert('Error saving task: ' + (error.response?.data?.message || 'Unknown error'));
      console.error('Task save error:', error);
    }
  };

  return (
    <div className="modal" onClick={(e) => e.target.classList.contains('modal') && onClose()}>
      <div className="modal-content">
        <div className="modal-header">
          <h2>{task ? 'Edit Task' : 'Add Task'}</h2>
          <button className="modal-close" onClick={onClose}>Ã—</button>
        </div>
        <div className="modal-body">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Title</label>
              <input
                className="form-control"
                value={form.title}
                onChange={(e) => setForm({ ...form, title: e.target.value })}
                placeholder="Enter task title"
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Description</label>
              <textarea
                className="form-control"
                rows="3"
                value={form.description}
                onChange={(e) => setForm({ ...form, description: e.target.value })}
                placeholder="Enter task description"
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Assigned Employee</label>
              <select
                className="form-control"
                value={form.assignedEmployee}
                onChange={(e) => setForm({ ...form, assignedEmployee: e.target.value })}
                required
              >
                <option value="">Select Employee</option>
                {employees.map(emp => (
                  <option key={emp._id} value={emp._id}>{emp.name}</option>
                ))}
              </select>
            </div>

            <div className="form-group">
              <label className="form-label">Priority</label>
              <select
                className="form-control"
                value={form.priority}
                onChange={(e) => setForm({ ...form, priority: e.target.value })}
                required
              >
                <option value="">Select Priority</option>
                <option value="High">High</option>
                <option value="Medium">Medium</option>
                <option value="Low">Low</option>
              </select>
            </div>

            <div className="form-group">
              <label className="form-label">Deadline</label>
              <input
                type="date"
                className="form-control"
                value={form.deadline}
                onChange={(e) => setForm({ ...form, deadline: e.target.value })}
                required
              />
            </div>

            <div className="form-group">
              <label className="form-label">Status</label>
              <select
                className="form-control"
                value={form.status}
                onChange={(e) => setForm({ ...form, status: e.target.value })}
                required
              >
                <option value="Pending">Pending</option>
                <option value="In Progress">In Progress</option>
                <option value="Completed">Completed</option>
              </select>
            </div>

            <div className="modal-actions">
              <button type="button" className="btn btn--outline" onClick={onClose}>Cancel</button>
              <button type="submit" className="btn btn--primary">
                {task ? 'Update Task' : 'Add Task'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}

export default TaskList;
