import React, {useEffect} from 'react';

const TaskList = ({tasks, onAdd, onEdit, onDelete}) => {
  const pri = (p)=> p==='High'?'priority-high':p==='Medium'?'priority-medium':'priority-low';
  const st = (s)=> s==='Pending'?'task-pending':s==='In Progress'?'task-progress':'task-completed';
  const fmt = (d)=> new Date(d).toLocaleDateString();

  useEffect(()=>{},[]);

  return (
    <section id="tasksSection" className="main-content">
      <div className="section-header">
        <h1>Task Management</h1>
        <button className="btn btn--primary" onClick={onAdd}>Add Task</button>
      </div>

      <div className="table-container">
        <table className="data-table">
          <thead>
            <tr>
              <th>Title</th><th>Assigned To</th><th>Priority</th><th>Status</th><th>Deadline</th><th>Actions</th>
            </tr>
          </thead>
          <tbody id="taskTableBody">
            {tasks.length===0 ? (
              <tr><td colSpan="6" className="text-center">
                <div className="empty-state"><div className="empty-icon">ðŸ“‹</div><p>No tasks found</p></div>
              </td></tr>
            ) : tasks.map(t=>(
              <tr key={t._id || t.id}>
                <td>
                  <div>
                    <strong>{t.title}</strong>
                    <div className="text-sm opacity-50">
                      {(t.description||'').slice(0,50)}{(t.description||'').length>50?'...':''}
                    </div>
                  </div>
                </td>
                <td>{t.assignedEmployeeName}</td>
                <td><span className={`status-badge ${pri(t.priority)}`}>{t.priority}</span></td>
                <td><span className={`status-badge ${st(t.status)}`}>{t.status}</span></td>
                <td>{fmt(t.deadline)}</td>
                <td>
                  <div className="action-buttons">
                    <button className="btn btn--sm btn--secondary" onClick={()=>onEdit(t)}>Edit</button>
                    <button className="btn btn--sm btn--outline" onClick={()=>onDelete(t)}>Delete</button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    </section>
  );
};

export default TaskList;
