import React, {useEffect} from 'react';
import StatCard from './StatCard';

const DashboardSection = ({employees, tasks, onInit}) => {
  useEffect(()=>{ onInit && onInit(); },[onInit]);
  const active = employees.filter(e=>e.status==='Active').length;
  const pending = tasks.filter(t=>t.status==='Pending').length;

  const activities = [
    { icon:'üë§', text:'New employee John Doe added', time:'2 hours ago' },
    { icon:'üìù', text:'Task "User Authentication" updated', time:'4 hours ago' },
    { icon:'‚úÖ', text:'Task "Onboarding Process" completed', time:'1 day ago' },
    { icon:'üè¢', text:'New department "Operations" created', time:'2 days ago' }
  ];

  return (
    <section id="dashboardSection" className="main-content">
      <div className="section-header"><h1>Dashboard</h1></div>
      <div className="stats-grid">
        <StatCard value={employees.length} label="Total Employees" icon="üë•" />
        <StatCard value={active} label="Active Employees" icon="‚úÖ" />
        <StatCard value={tasks.length} label="Total Tasks" icon="üìã" />
        <StatCard value={pending} label="Pending Tasks" icon="‚è≥" />
      </div>

      <div className="card mt-32">
        <div className="card__body">
          <h3>Recent Activities</h3>
          <div>
            {activities.map((a,i)=>(
              <div className="activity-item" key={i}>
                <div className="activity-icon">{a.icon}</div>
                <div className="activity-content">
                  <p>{a.text}</p>
                  <div className="activity-time">{a.time}</div>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>
    </section>
  );
};

export default DashboardSection;
