import React, { useEffect, useState } from 'react';
import axios from 'axios';

export default function EmployeeList() {
  const [employees, setEmployees] = useState([]);
  const [query, setQuery] = useState('');
  const [showModal, setShowModal] = useState(false);
  const [editEmployee, setEditEmployee] = useState(null);

  // Fetch employees
  const fetchEmployees = async () => {
    try {
      const response = await axios.get('/api/employees');
      setEmployees(response.data);
    } catch (error) {
      console.error('Error:', error);
    }
  };

  useEffect(() => {
    fetchEmployees();
  }, []);

  // Filter employees
  const filteredEmployees = employees.filter(emp =>
    emp.name.toLowerCase().includes(query.toLowerCase()) ||
    emp.email.toLowerCase().includes(query.toLowerCase()) ||
    emp.department.toLowerCase().includes(query.toLowerCase())
  );

  // Delete employee
  const handleDelete = async (id, name) => {
    if (window.confirm(`Delete ${name}?`)) {
      try {
        await axios.delete(`/api/employees/${id}`);
        fetchEmployees(); // Refresh list
        alert('Employee deleted!');
      } catch (error) {
        alert('Error deleting employee');
      }
    }
  };

  return (
    <div id="employeesSection" className="main-content">
      <div className="section-header">
        <h1>Employee Management</h1>
        <button 
          className="btn btn--primary" 
          onClick={() => { setEditEmployee(null); setShowModal(true); }}
        >
          Add Employee
        </button>
      </div>

      <div className="search-container">
        <input
          className="form-control"
          placeholder="Search employees by name, email, or department..."
          value={query}
          onChange={(e) => setQuery(e.target.value)}
        />
      </div>

      <div className="table-container">
        <table className="data-table">
          <thead>
            <tr>
              <th>Name</th><th>Email</th><th>Phone</th><th>Department</th><th>Status</th><th>Actions</th>
            </tr>
          </thead>
          <tbody>
            {filteredEmployees.map(emp => (
              <tr key={emp._id}>
                <td>{emp.name}</td>
                <td>{emp.email}</td>
                <td>{emp.phone}</td>
                <td>{emp.department}</td>
                <td>
                  <span className={`status-badge ${emp.status === 'Active' ? 'status-active' : 'status-inactive'}`}>
                    {emp.status.toUpperCase()}
                  </span>
                </td>
                <td>
                  <div className="action-buttons">
                    <button 
                      className="btn btn--sm btn--secondary" 
                      onClick={() => { setEditEmployee(emp); setShowModal(true); }}
                    >
                      Edit
                    </button>
                    <button 
                      className="btn btn--sm btn--outline" 
                      onClick={() => handleDelete(emp._id, emp.name)}
                    >
                      Delete
                    </button>
                  </div>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {showModal && (
        <EmployeeModal
          employee={editEmployee}
          onClose={() => setShowModal(false)}
          onSave={fetchEmployees}
        />
      )}
    </div>
  );
}

// Simple Modal Component
function EmployeeModal({ employee, onClose, onSave }) {
  const [form, setForm] = useState({
    name: employee?.name || '',
    email: employee?.email || '',
    phone: employee?.phone || '',
    department: employee?.department || '',
    status: employee?.status || 'Active'
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    try {
      if (employee) {
        // Update existing
        await axios.put(`/api/employees/${employee._id}`, form);
        alert('Employee updated!');
      } else {
        // Create new
        await axios.post('/api/employees', form);
        alert('Employee added!');
      }
      
      onSave(); // Refresh list
      onClose(); // Close modal
    } catch (error) {
      alert('Error saving employee: ' + (error.response?.data?.message || 'Unknown error'));
    }
  };

  return (
    <div className="modal" onClick={(e) => e.target.classList.contains('modal') && onClose()}>
      <div className="modal-content">
        <div className="modal-header">
          <h2>{employee ? 'Edit Employee' : 'Add Employee'}</h2>
          <button className="modal-close" onClick={onClose}>×</button>
        </div>
        <div className="modal-body">
          <form onSubmit={handleSubmit}>
            <div className="form-group">
              <label className="form-label">Name</label>
              <input 
                className="form-control" 
                value={form.name} 
                onChange={(e) => setForm({...form, name: e.target.value})}
                required
              />
            </div>
            
            <div className="form-group">
              <label className="form-label">Email</label>
              <input 
                type="email"
                className="form-control" 
                value={form.email} 
                onChange={(e) => setForm({...form, email: e.target.value})}
                required
              />
            </div>
            
            <div className="form-group">
              <label className="form-label">Phone</label>
              <input 
                className="form-control" 
                value={form.phone} 
                onChange={(e) => setForm({...form, phone: e.target.value})}
                required
              />
            </div>
            
            <div className="form-group">
              <label className="form-label">Department</label>
              <select 
                className="form-control" 
                value={form.department} 
                onChange={(e) => setForm({...form, department: e.target.value})}
                required
              >
                <option value="">Select Department</option>
                <option value="Engineering">Engineering</option>
                <option value="Marketing">Marketing</option>
                <option value="HR">HR</option>
                <option value="Sales">Sales</option>
                <option value="Finance">Finance</option>
                <option value="Operations">Operations</option>
              </select>
            </div>
            
            <div className="form-group">
              <label className="form-label">Status</label>
              <select 
                className="form-control" 
                value={form.status} 
                onChange={(e) => setForm({...form, status: e.target.value})}
              >
                <option value="Active">Active</option>
                <option value="Inactive">Inactive</option>
              </select>
            </div>
            
            <div className="modal-actions">
              <button type="button" className="btn btn--outline" onClick={onClose}>Cancel</button>
              <button type="submit" className="btn btn--primary">
                {employee ? 'Update Employee' : 'Add Employee'}
              </button>
            </div>
          </form>
        </div>
      </div>
    </div>
  );
}
